mapper.select:

  private String region;          // 지역
  private String title;           // 숙소명
  private String addr;            // 주소
  private String pic1_uuid;       // 수속 이미지 #1
  private String typeOfAcco;      // 숙박시설 유형
  private String typeOfAccoName;  // 숙박시설 유형
  private int weekdayPrice;  typeOfAccoName     // 주중가격
  private int weekendPrice;       // 주말가격
  private int star;               // 별점
    
  SELECT m.*, d.*, c.name as type_of_accoName
  FROM ( SELECT region, title, addr, pic1_uuid, type_of_acco   
         FROM   room_mst
          
            
              (
            
            
            
              OR type_of_acco = #{isHotel} 
             
            
              OR type_of_acco = #{isMotel} 
             
            
              OR type_of_acco = #{isPension} 
             
            
              OR type_of_acco = #{isGlamping} 
             
            
              OR type_of_acco = #{isGuestHouse} 
            
            
              OR type_of_acco = #{isResort} 
            
          
            
              )
            
          
            
              AND (region LIKE '%'|| #{keyword} ||'%'
                OR title  LIKE '%'|| #{keyword} ||'%'
                OR addr   LIKE '%'|| #{keyword} ||'%'
              )
             
          
  ) m
  INNER JOIN code c
  ON    c.class1 = 'ROOM' 
  AND   c.class2 = 'TYPE_OF_ACCO'
  AND   c.class3 = m.type_of_acco
  INNER JOIN (
    SELECT * 
    FROM   room_dtl
    WHERE (weekday_price BETWEEN #{minweekdayPrice} AND #{maxweekdayPrice}
    OR     weekend_price BETWEEN #{minweekendPrice} AND #{maxweekendPrice})
    AND    num_of_guests = #{numOfGuests}
    ) d
  ON    m.id = d.mst_id
