mapper.select:
SELECT * FROM (
SELECT m.*, d.*, c.name as type_of_acco_name , rownum as num FROM ( SELECT id, region, title, addr, pic1_uuid, type_of_acco FROM room_mst
#{item}
AND (region LIKE '%'|| #{keyword} ||'%' OR title LIKE '%'|| #{keyword} ||'%' OR addr LIKE '%'|| #{keyword} ||'%' )
) m INNER JOIN code c ON c.class1 = 'ROOM' AND c.class2 = 'TYPE_OF_ACCO' AND c.class3 = m.type_of_acco INNER JOIN ( SELECT id as dtl_id, mst_id, weekday_price, weekend_price, CASE num_of_revu WHEN 0 THEN 0 ELSE sum_of_stars / num_of_revu END as star FROM room_dtl
AND (weekday_price BETWEEN #{minPrice} AND #{maxPrice} OR weekend_price BETWEEN #{minPrice} AND #{maxPrice})
AND (weekday_price gte #{minPrice} OR weekend_price gte #{minPrice})
AND (weekday_price lte #{minPrice} OR weekend_price lte #{minPrice})
AND num_of_guests = #{numOfGuests}
) d ON m.id = d.mst_id
INNER JOIN rsrv r ON ( (#{checkIn} < check_in AND #{checkOut} < check_in) OR (#{checkIn} > check_out AND #{checkOut} > check_out) ) AND r.room_dtl_id = d.id
INNER JOIN rsrv r ON #{checkIn} < check_in AND #{checkIn} > check_out AND r.room_dtl_id = d.id
INNER JOIN rsrv r ON #{checkOut} < check_in AND #{checkOut} > check_out AND r.room_dtl_id = d.id
ORDER BY
star, num_of_revu, weekday_price, weekend_price num_of_revu, star, weekday_price, weekend_price weekday_price, weekend_price, num_of_revu, star weekday_price, weekend_price, star
weekday_price, weekend_price, star
) WHERE num BETWEEN (#{page}-1) * #{perPage}+1 AND #{page}*#{perPage}
