mapper.select:
SELECT role, name, nickname
FROM users
WHERE id=#{id} AND pwd=#{pwd}

mapper.insert:
INSERT INTO users
(
id, pwd, name, nickname,
tel, email, gen, role, c_user
)
VALUES
(
#{id}, #{pwd}, #{name}, #{nickname},
#{tel}, #{email}, #{gen}, 'U', #{id}
)

mapper.update:
UPDATE users
SET pwd=#{pwd}, nickname=#{nickname},
	tel=#{tel}, email=#{email}
WHERE id=#{id}

UPDATE users
SET pwd=#{pwd}, nickname=#{nickname},
	tel=#{tel}, email=#{email}
WHERE id=#{id}
